int ptr_map(int matrizUniverso[][MXJU], int x){
    int i, j, flag;

    flag=0;
    gotoxy(1, 2);
    if(x+MXPTR!=MXJU){
        for(i=0; i<MXIU; i++){
            for(j=0+x; j<MXPTR+x; j++){
                if(matrizUniverso[i][j]!=0)
                    printf("%d", matrizUniverso[i][j]);
                else printf(" ");
            }
            printf("\n");
        }
    }else{
        flag=1;
    }

    return flag;
}
